import React, { useState } from 'react';
import './CSS/Knives.css';
import all_knives from '../Components/Assets/all_knifes';
import Item from '../Components/Knife/Knife';

export const Knives = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [knifeType, setKnifeType] = useState('');
    const [minPrice, setMinPrice] = useState('');
    const [maxPrice, setMaxPrice] = useState('');

    const handleKnifeTypeChange = (event) => {
        setKnifeType(event.target.value);
    };

    const handleMinPriceChange = (event) => {
        setMinPrice(event.target.value);
    };

    const handleMaxPriceChange = (event) => {
        setMaxPrice(event.target.value);
    };

    const handlePriceFilter = (min, max) => {
        setMinPrice(min);
        setMaxPrice(max);
    };

    const filteredKnives = all_knives.filter((knife) => {
        const matchesSearchQuery = knife.name.toLowerCase().includes(searchQuery.toLowerCase());
        const matchesKnifeType = knifeType === '' || knife.type === knifeType;
        const matchesMinPrice = minPrice === '' || knife.knife_price >= minPrice;
        const matchesMaxPrice = maxPrice === '' || knife.knife_price <= maxPrice;

        return matchesSearchQuery && matchesKnifeType && matchesMinPrice && matchesMaxPrice;
    });

    return (
        <div className='knives'>
            <h1>All Knives</h1>
            <input
                type="text"
                placeholder="Search for Knives..."
                className="search-bar"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
            />
            <div className="filters">
                <select onChange={handleKnifeTypeChange} value={knifeType}>
                    <option value="">All Types</option>
                    {/* Add options for each knife type */}
                    <option value="bayonet">Bayonet</option>
                    {/* ...other knife types */}
                </select>
                <div className="price-filter">
                    <input
                        type="number"
                        placeholder="Min Price"
                        value={minPrice}
                        onChange={handleMinPriceChange}
                    />
                    <input
                        type="number"
                        placeholder="Max Price"
                        value={maxPrice}
                        onChange={handleMaxPriceChange}
                    />
                </div>
                <div className="price-dropdown">
                    <button onClick={() => handlePriceFilter(100, '')}>$100+</button>
                    <button onClick={() => handlePriceFilter(200, '')}>$200+</button>
                    {/* ...other price ranges */}
                </div>
            </div>
            <div className='knife-grid'>
                <div className='all-knives'>
                    {filteredKnives.map((item, i) => (
                        <Item
                            key={i}
                            id={item.id}
                            name={item.name}
                            image={item.image}
                            knife_price={item.knife_price}
                        />
                    ))}
                </div>
            </div>
        </div>
    );
}

export default Knives;
