import React, { useState } from 'react';
import './CSS/Knives.css';
import all_knives from '../Components/Assets/all_knifes';
import Item from '../Components/Knife/Knife';
import Filters from '../Components/Filters/Filters';

const ITEMS_PER_PAGE = 30;

export const Knives = () => {
  const [currentPage, setCurrentPage] = useState(1);
  const [filters, setFilters] = useState({ minPrice: '', maxPrice: '', knifeType: '' });

  const handleFilterChange = (newFilters) => {
    setFilters(newFilters);
    setCurrentPage(1);  // Reset to first page when filters change
  };

  const filteredKnives = all_knives.filter(knife => {
    const withinPriceRange = 
      (filters.minPrice === '' || knife.knife_price >= filters.minPrice) &&
      (filters.maxPrice === '' || knife.knife_price <= filters.maxPrice);

    const matchesType = 
      filters.knifeType === '' || knife.name.toLowerCase().includes(filters.knifeType);

    return withinPriceRange && matchesType;
  });

  const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
  const endIndex = startIndex + ITEMS_PER_PAGE;
  const currentItems = filteredKnives.slice(startIndex, endIndex);

  const totalPages = Math.ceil(filteredKnives.length / ITEMS_PER_PAGE);

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  return (
    <div className='knives'>
      <h1>All Knives</h1>
      <Filters onFilterChange={handleFilterChange} />
      <div className='knife-grid'>
        <div className='all-knives'>
          {currentItems.map((item, i) => (
            <Item key={i} id={item.id} name={item.name} image={item.image} knife_price={item.knife_price} />
          ))}
        </div>
      </div>
      <div className='pagination'>
        <button onClick={handlePrevPage} disabled={currentPage === 1}>Previous</button>
        <span>Page {currentPage} of {totalPages}</span>
        <button onClick={handleNextPage} disabled={currentPage === totalPages}>Next</button>
      </div>
    </div>
  );
};

export default Knives;
